mapper.select:
select gCode, gCategory, gName, gPrice, gImage, gAmount, userid
from cart
where userid = #{userid} 
and gCode = #{gCode}

mapper.update:
 update cart
 set gAmount = #{gAmount}
where gCode = #{gCode}

