mapper.select:
select count(*)
from member
where userid = #{userid}

select userid,password, username,post, addr1, addr2, phone,
email1, email2, mas, ssn, hiredate, mileage
from member
where username = #{username} 
and email1 = #{email1}
and email2 = #{email2}

select userid,password, username,post, addr1, addr2, phone,
email1, email2, mas, ssn, hiredate, mileage
from member
where username = #{username} 
and userid = #{userid}
and email1 = #{email1}
and email2 = #{email2}

mapper.update:
update member
set password = #{password}
where userid = #{userid}
